module.exports = function (board, jogada, parent = root) {
    
    
    this.number = ++numjogo;
    
    this.tree = parent.tree + ':' + this.number;
    
    this.acabado = false;
    
    this.jogadas = ++jogada;
    
    this.vitoria = null;
    
    this.board = board;
    
    this.jogado = false;
    
    this.parent = parent;


    //this.jogaveis = jogaveis; /* funcao que devolve os jogaveis com base no board */
}